// проверяем что передали не пустую строку
function checkStringNotEmpty(word) {
  return (typeof word === 'string')
    ? !!word.length
    : false;
}

// [^a-z']
function checkExtraSymbols(word) {
  var test = /[^a-z']/g

  if (test.test(word)) {
    return false;
  }

  return true;
}

