// апостроф в конце
function checkWordsWithApoEnd(word) {
  if (/'s\b/.test(word) && word.length > 3) {

    // если в конце слова несколько 's's's тогда не проходит
    if (word.indexOf("'s") != word.length - 2) {
      return false;
    }

    // убираем из конца слова 's
    word = word.substring(0, word.indexOf("'s"));

    // если есть еще один апостроф - не проходит
    if (/'/.test(word)) {
      return false;
    }

    // возвращаем обрезанное слово без 's
    return word;
  }

  return false;
}

// d' / mc / mac префиксы
function checkWordsWithApoStart(word) {
  if (/\b((d')|(d(a?)c))/.test(word) && word.length > 3) {

    word = word.replace(/\b((d')|(m(a?)c))/, '');

    // возвращаем слово без префиксов
    return word;
  }

  return word;
}

